/*global $*/
//rewrite this withput using jquery
export function get(...args){
    return promise($.get(...args),args);
}
export function post(...args){
    return promise($.post(...args),args);
}
export function promise(p,args){
    return new Promise((s,e)=>{
        p.then(s,w(e,args));    
    });
}
function w(rej,args){
    return function (_e){
        console.log("args",args);
        let e=new Error(_e.responseText);
        e.response=_e;
        return rej(e);
    };
}