import {sibling} from "@acepad/here";
export default function(...a){
  let file;
  if(typeof a[0].path==="function")file=a.shift();
  else file=sibling(...a);
  const id=file.path().replace(/\W/g,"-");
  const cont=file.text();
  const ct=file.lastUpdate()+"";
  const e=document.querySelector("#"+id);
  if(e){
      const t=e.getAttribute("data-utime");
      if(t===ct)return;
      e.setAttribute("data-utime",ct);
      e.innerHTML=cont;
      return ;
  }
  const s=document.createElement("style");
  s.setAttribute("id",id);
  s.setAttribute("data-utime",ct);
  s.innerHTML=cont;
  document.body.appendChild(s);
} 