// acepad-dotenv
import {file} from "@acepad/here";
import {match} from "textmatcher";
export function config({path}){
    if(typeof path==="string"){
        path=file(path);
    }
    if(path.isDir()){
        path=path.rel(".env");
    }
    for(let line of path.lines()){
        match(line,/\s*(\w+)\s*=(.*)/,(m)=>{
            //console.log("setEnv",m[1],m[2]);
            process.env[m[1]]=m[2];
        });
    }
}
export async function configUp(e){
    let f;
    while(e){
        f=e.rel(".env");
        if(f.exists())break;
        f=null;
        e=e.up();    
    }
    if(f){
        config({path:f});
    }
}    
