//!run 
import * as dotenv from "@acepad/dotenv";
export async function main(){
    let e=this.resolve("./");
    let f;
    while(e){
        f=e.rel(".env");
        if(f.exists())break;
        f=null;
        e=e.up();    
    }
    if(f){
        dotenv.config({path:f});
        
    }
    
}    
