import * as dotenv from "@acepad/dotenv";
import * as assert from "assert";
export async function main(){
    let e=this.resolve("test/.env");
    assert.ok(e.exists(),e.path()+" not found");
    dotenv.config({path:e});
    assert.equal(this.get("TEST"),"foo","TEST");
    assert.equal(this.get("TOAST"),"yummy","TOAST");
    
}    
