import {findSessionByFile} from "./acepad-files.js";
import DefaultMap from "@hoge1e3/default-map";
export function setAnnotations(acepad, annotations){
    const m=new DefaultMap(()=>([]),true);
    for(let a of annotations){
        const s=findSessionByFile(acepad, a.file);
        if(!s)return ;
        m.get(a.file+"").push(a);
    }
    for(let [k,v] of m){
        const s=findSessionByFile(acepad, k);
        s.setAnnotations(v);
    }
}