import {render} from "./render.js";
let w;
export function main(){
  if(w){
    w.show();
    return ;
  }
  w=show("<div>",{width:50,buttons:[
      //{command:"close",caption:"x"},
      {command:"minify",caption:"_"},
  ],noMenu:true,});
  const sh=this;
  const home=sh.resolve(sh.$home);
  const e=render(home,
    (f)=>sh.edit(f,{nomin:true}),
  );
  w.print(e);
}
