// @acepad/here
import {urlToPath,file as f} from "petit-node";
// imu=import.meta.url
export function dir(imu){
    return file(imu).up();   
}
export function file(imu){
    if (isFile(imu)) return imu;
    return f(urlToPath(imu));
}
export function sibling(imu,p){
    return dir(imu).rel(p);
}
export function isFile(imu) {
    return imu && (typeof (imu.rel))==="function";
}
export function dirPath(imu){
  return dir(imu).path();
}
export function filePath(imu){
  return file(imu).path();
}
export function siblingPath(imu){
  return sibling(imu).path();
}
