// @acepad/cdn
import {loadScriptTag} from "@hoge1e3/loadScript";
const cdn=process.env.cdn_url||"https://cdn.jsdelivr.net/npm/";//"https://unpkg.com/"
export function load(...scripts) {
    return Promise.all(scripts.
    map(s=>`${cdn}${s}`).
    map(async (u)=>{
        try{
            //console.log("Loading ",u);
            await loadScriptTag(u);
            //console.log("Loaded ",u);
        }catch(e){
            alert("load fail"+e.message);
        }
    }));
}
