import {update} from "./deps.js";
export async function main(o={}) {
    if(o.a){
        for(let f of this.resolve(".").listFiles()){
            if(!f.isDir())continue;
            if(!f.rel("package.json").exists())continue;
            await this.echo(f.name());
            await update(f);
        }
        return ;
    }
    return update(this.resolve("."));
}