#!run
import {tokenizeLines} from "@acepad/pysh" ;
export async function main(){
  const src=this.resolve("pyshell.py").text();
  const tokens=(tokenizeLines(src));
  for (let token of tokens){
    this.echo((
      token.type=="WORD"?token.value:token.type
    )+",", {n:token.type!=="NEWLINE"});
  }
  //return JSON.stringify(tokens,null,2);
}