// acepad-mode-shell
import {createMode} from "@acepad/mode";
//import {sessionInfo} from "@acepad/sessions";
let inited;
export function init(){
  if(inited)return;
  inited=true;
  createMode("sessions",{
    highlights:[
      {
      regex:/^\[Recents\]/,
      css: "color:blue; font-weight:bold;"
      },
      {
      regex:/^\[Session List\]/,
      css: "color:#00c; font-weight:bold;"
      },
      {
      regex:/^[^\t]*\//,
      css: "color:green; "
      },
      {
      regex:/\t.*$/,
      css: "color:gray; "
      },
    ],
    //getCompletions
  });
}
