#!run
// @acepad/sketch
import {show} from "@acepad/widget";
import {generator} from "@hoge1e3/dom";
import css from "@acepad/css";
export function main(){
    const dir=this.resolve(this.$sketchbook||
    this.$home+"/sketchbook");
    let newfile=createFilename();//"0000_0000_000000.jpg";
    let filename=newfile;
    const w=show({fullscreen:true});
    w.element.style["background-color"]="gray";
    const t=generator();
    const density=2;
    const bg="#fff";
    let erase;
    css(this.resolve("./sketch.css"));
    const vps=[800,600];
    const vpm=mul(vps,0.5);
    const cs=mul(vps,density);//[width*density,height*density];
    const d=showMenu();

    const vp=w.print(t.div(
        {
            class: "sketch-viewport",
            ontouchstart,
            ontouchmove,
            ontouchend,
        },t.div({
            class: "sketch-transformer",
        },t.canvas({
            width:cs[0],
            height:cs[1],
        })))
    );
    const transformer=vp.querySelector(
        ".sketch-transformer");
    const c=vp.querySelector("canvas");
    
    Object.assign(vp.style,{
        left:`${0}px`,
        top:`${0}px`, 
        width:`${vps[0]}px`,
        height:`${vps[1]}px`,
    });
    const ctx=c.getContext("2d");
    const hcs=mul(cs,0.5);
    const cofs=mul(sub(vps,cs),0.5);
    Object.assign(c.style,{
        position:"absolute",
        left:`${cofs[0]}px`,
        top:`${cofs[1]}px`, 
        width:`${cs[0]}px`,
        height:`${cs[1]}px`,
    });
    
    ctx.translate(...hcs);
    ctx.strokeStyle="black";
    load(dir.rel(filename));
    ctx.globalAlpha=0.5;
    function showMenu(){
        console.log("showMenu",filename);
        return w.print(t.div({
            id:"sketch-menu",
            class:"over-canvas",
        },
        t.button({onclick:setPen},erase?"Erase":"Pen"),
        t.span(filename),
        t.button({onclick:prev},"<<"),
        t.button({onclick:next},">>"),
        ));
    }
    function setPen(){
        erase=!erase;
        if(erase){
            ctx.strokeStyle=bg;
            ctx.lineWidth=50/transform.scale;
        }else{
            ctx.strokeStyle="black";
            ctx.lineWidth=1;
        }
        showMenu();
    }
    //let px,py;
    let pinch;
    let transform={scale:1,translate:[0,0]};
    let tcount=0;
    let pivot;
    function ontouchstart(e){
        tcount++;
        console.log("ontouchstart");
        const p0=client(e.touches[0]);
        if(e.touches.length>1){
            const p1=client(e.touches[1]);
            pinch={
                d:dist(p1,p0),
                m:mid(p1,p0)
            };
            pivot=conv(pinch.m,transform);
            e.stopPropagation();
            e.preventDefault();
            return ;
        }
        ctx.beginPath();
        const [dx,dy]=conv(p0,transform);
        ctx.moveTo(dx,dy);
        e.stopPropagation();
        e.preventDefault();
            
        //[px,py]=[x,y];
    }
    function ontouchmove(e){
        const p0=client(e.touches[0]);
        if(e.touches.length>1&&pinch){
            const p1=client(e.touches[1]);
            const pinch2={
                d:dist(p1,p0),
                m:mid(p1,p0)
            };
            transform.scale*=pinch2.d/pinch.d;
            const pivot2=conv(pinch2.m,transform);
            const m=sub(pivot2,pivot);//sub(pinch2.m, pinch.m);
            //console.log(pinch,pinch2,mx,my);
            transform.translate=add(
                transform.translate,
                mul(m,0.5));
            //c.style.transform = `scale(${transform.scale}) translate(${transform.x}px,${transform.y}px)`;
            transformer.style.transform = trstr(transform);
            //` translate(${transform.translate[0]}px,${transform.translate[1]}px) scale(${transform.scale})`;
            //console.log(`scale(${transform.scale}) translate(${transform.x},${transform.y})`);
            pinch=pinch2;
            e.stopPropagation();
            e.preventDefault();
            return;
        }
        if(pinch)return ;

        const [dx,dy]=conv(p0,transform);
        ctx.lineTo(dx,dy);
        ctx.stroke();
        ctx.beginPath();    
        ctx.moveTo(dx,dy);
        e.stopPropagation();
        e.preventDefault();
    }
    function ontouchend(e){
        tcount--;
        console.log("tend",tcount,e.touches);
        //ctx.endPath();
        if(e.touches.length<=0){
            pinch=null;
            if(erase){
                ctx.strokeStyle=bg;
                ctx.lineWidth=50/transform.scale;
            }
            save(dir.rel(filename));
        }
    }
    function client(e){
        return sub([e.clientX,e.clientY],vpm);
    }
    async function prev(){
        const n=older()||filename;
        console.log("prev",n);
        if(n===filename)return ;
        await load(dir.rel(n));
    }
    function older(){
        const f=files(dir);
        let i=f.indexOf(filename);
        console.log("older",f,filename,i);
        if(i<0)return f[f.length-1]||newfile;
        return f[i-1];
    }
    async function next(){
        const n=newer()||filename;
        if(n===filename)return ;
        await load(dir.rel(n));
    }
    function newer(){
        const f=files(dir);
        let i=f.indexOf(filename);
        if(i<0)return newfile;
        return f[i+1]||newfile;
    }
    function save(file){
        const d=c.toDataURL('image/jpeg');
        file.dataURL(d);
        filename=file.name();
        showMenu();
        if(filename===newfile){
            newfile=createFilename();
        }
    }
    async function load(file){
        ctx.fillStyle=bg;
        ctx.globalAlpha = 1;
        ctx.clearRect(...mul(hcs,-1),...cs);
        ctx.fillRect(...mul(hcs,-1),...cs);
        ctx.globalAlpha = 0.5;
        filename=file.name();
        showMenu();
        if(!file.exists())return;
        return new Promise(s=>{
            // create img element
            const im = new Image();
            // Ensure the image is loaded before drawing
            im.onload = function () {
                console.log("im", im.src); // src should be correct
                ctx.globalAlpha = 1;
                ctx.drawImage(im, ...mul(hcs,-1));
                ctx.globalAlpha = 0.5;
                s();
            };
            im.src = file.dataURL();
        });
    }
}
function trstr(transform){
    const t=`translate(${transform.translate[0]}px,${transform.translate[1]}px)`;
    const s=`scale(${transform.scale})`;
    return t+" "+s;
}
function conv(p,t){
    return mul(sub(p,t.translate),1/t.scale);
}
function mid(a,b){
    return a.map((v,i)=>((v+b[i])/2));
}
function sub(a,b){
    return a.map((v,i)=>(v-b[i]));
}
function add(a,b){
    return a.map((v,i)=>(v+b[i]));
}
function mul(a,k){
    return a.map(v=>v*k);
}
function dist(a,b){
    const [x,y]=sub(a,b);
    return Math.sqrt(x**2+y**2);
}
function files(dir){
    return [...dir.listFiles()].
    filter(f=>f.ext()===".jpg").sort(
        (a,b)=>a.lastUpdate()-b.lastUpdate()).
        map(f=>f.name());
}
function createFilename() {
    // Get current date and time
    let now = new Date();
    
    // Format components with leading zeros if necessary
    let yyyy = now.getFullYear();
    let MM = String(now.getMonth() + 1).padStart(2, '0'); // Months are 0-based
    let dd = String(now.getDate()).padStart(2, '0');
    let HH = String(now.getHours()).padStart(2, '0');
    let mm = String(now.getMinutes()).padStart(2, '0');
    let ss = String(now.getSeconds()).padStart(2, '0');

    // Construct the filename
    return `${yyyy}_${MM}${dd}_${HH}${mm}${ss}.jpg`;
}

