import {compileProject} from "@acepad/tsc";
import * as assert from "assert";
export async function main(){
    const prj=this.resolve("test/testprj/");
    //console.log(localStorage[prj.path()]);
    for(let f of prj.recursive()){
        //this.echo(f);
        if(f.ext()===".js") f.rm();
        if(f.endsWith(".js.map")) f.rm();
    }
    const err=compileProject(prj);
    console.log("error",err);
    for(let f of prj.recursive()){
        if(f.ext()===".js") {
            this.echo(f);
            this.echo(f.text());
        }
    }
}    
