export async function getPackageJson(){
  const r=await fetch("./gen/boot/package.json");
  return await r.json();
}
export async function setPackageJson(pk){
  const tmpf=this.resolve("/tmp/package.json");
  tmpf.obj(pk);
  const newurl=new URL("./gen/boot/package.json",
  location.href);
  await this.serve(tmpf,newurl+"");
}
