export interface ShowIframeOptions {
  name?: () => string;
  text?: () => string;
}

export interface WidgetOptions {
  element?: string | HTMLElement;
  acepad?: any;
  buttons?: { command: string | (() => void); caption: string }[];
  noMenu?: boolean;
  width?: number;
  fullscreen?: boolean;
  overlay?: boolean;
}

export interface Widget {
  element: HTMLElement;
  show: () => void;
  hide: () => void;
  close: () => void;
  minify: () => void;
  fire: (event: string) => void;
  closed?: boolean;
  updater: any;
  iframe?: HTMLIFrameElement;
  go?: (url: string) => void;
}

export function showIframe(url: string | ShowIframeOptions, acepad?: any): Widget;
export function showWidget(e?: string | HTMLElement | WidgetOptions, opt?: WidgetOptions): Widget;
export function minifyAll(): void;
export const show: typeof showWidget;
