#!run
import * as rpc from 
    "https://cdn.jsdelivr.net/npm/@hoge1e3/rpc@latest/dist/index.js";
export async function main(){
    const w=new BroadcastChannel("test");
    this.echo("done");
    rpc.proxy.server(w,{
        test(x){
            return x+3;
        }
    });
    const c=rpc.proxy.client(w,"default");
    this.echo(await c.test(20));
}