// @hoge1e3/counter
import DefaultMap from "@hoge1e3/default-map";
export var Counter=class extends DefaultMap{
    constructor(){
        super(0);
    }
    add(k,by=1){
        this.set(k,this.get(k)+by);
    }
    ascend(){
        return [...this].
        sort(([v,a],[v2,b])=>a-b);
    }
    descend(){
        return [...this].
        sort(([v,a],[v2,b])=>b-a);
    }
};
export default Counter;


