// @hoge1e3/dom-bind
export const BoundObject=Symbol("BoundObject");
export function bind(e,o){
    if(typeof e==="string")e=document.querySelector(e);
    //console.log("bind",e,o);
    e[BoundObject]=o;
    //o.element=e;
    return o;
}
export function bounded(e){
    if(typeof e==="string")e=document.querySelector(e);
    //console.log("bounded!",e,e[BoundObject]);
    return e[BoundObject];
}
export function unbind(e){
    if(typeof e==="string")e=document.querySelector(e);
    if(e[BoundObject]){
        delete e[BoundObject];
    }
}