export type Remover={
    remove():void;
};
export class SingleTypeEventHandler {
    constructor(type:string);
    on(...args:any[]):Remover;
    fire(...args:any[]):void;
}
export class EventHandler {
    constructor();
    on(type:string, ...args:any[]):Remover;
    fire(type:string, ...args:any[]):void;
    static attachTo<T>(o:T):T&EventHandler;
}