#!run
// @hoge1e3/hourglass
import css from "@acepad/css";
export function main(){
  css(import.meta.url,"hourglass.css");
  
  const alertEl=document.createElement("div");
  alertEl.setAttribute("id","hourglass");
  alertEl.innerText="⌛";
  document.body.appendChild(alertEl);
  
  
  function resetAnimation() {
    alertEl.style.animation = "none";
    // 再計算を発生させてアニメーションを再適用
    alertEl.offsetHeight; 
    alertEl.style.animation = "";
  }
  
  // JSが動いている間は、アニメーションを定期的にリセット
  setInterval(() => {
    resetAnimation();
  }, 100); // CSSのアニメ時間（2s）より短ければOK
}