import t from "@hoge1e3/html-builder";
import {showWidget} from "acepad-widget";

export async function main(){
    let w=showWidget("<div>");
    t(w.element,(t)=>{
        t.font({color:"red"},"hoge");
        t.br();
        t.button({onclick:"alert(3);"},(t)=>{
            t("ok");
        });
        t("fuga");  
        t.ul((t)=>{
            for(let i=0;i<10;i++){
                t.li(i+"");
            }
        });
        
    });
}
