import t from "@hoge1e3/html-builder";
import {showWidget} from "acepad-widget";

export async function main(){
    let w=showWidget("<div>");
    w.element.appendChild(t.div(
        t.font({color:"red"},"hoge"),
        t.br(),
        t.button({onclick:"alert(3);"},"ok"),
        "hoge",
        t.ul([2,3,5].map(s=>t.li(s))),
        "fuga",
        t.ul((a)=>{
            for(let i=0;i<3;i++){
                a.li(i+"");
            }
        })
    ));
}
function custom(){
    t.custom("items",function (...args) {
        let t=this;
        let target=t.ul();
        let nt=t.clone({
            target,
            applyPrimitive(s){
                this.li(s);
            },
        }); 
        nt(...args);
        return target;
    });
    t.items((t)=>{
        t("hoge");
        t((t)=>{
            t.font({color:"red"},"fuga");
        });
    });
}