// @hoge1e3/idle-timer
import {EventHandler} from "@hoge1e3/events";
export function timer(timeout){
    const self={
        active(){
            if(self.t)clearTimeout(self.t);
            self.t=setTimeout(fire,timeout);        
        }
    };
    const fire=()=>{
        self.fire("idle");
    };
    EventHandler.attachTo(self);
    //self.active();
    return self;
}