export interface IdleTimer {
  /** Call this to reset the idle timeout timer */
  active(): void;
  /** Listen for the 'idle' event */
  on(event: "idle", callback: () => void): void;
  //off(event: "idle", callback: () => void): void;
}

/**
 * Creates an idle timer that emits 'idle' event after the given timeout.
 * @param timeout Timeout in milliseconds
 */
export function timer(timeout: number): IdleTimer;
