import {push,modifierState,setTarget,sync} from "@hoge1e3/key-stream";
import * as assert from "assert";
function str(e){
  return `${e.key} ${e.shiftKey}  ${e.ctrlKey}`
}
export async function main(){
  const e=new EventTarget();
  setTarget(e);
  e.addEventListener("down",(e)=>{
    console.log("down",str(e));
  })
  e.addEventListener("up",(e)=>{
    console.log("up",str(e));
  })
  
    push({type:"down",key:"Shift"});
//    console.log(JSON.stringify(modifierState));
    push({type:"down",key:"A"});
    push({type:"up",key:"A"});
    push({type:"up",key:"Shift"});
    
    sync({ctrlKey:true});
    sync({ctrlKey:false,shiftKey:true});
}    
