// @hoge1e3/loadScript
import {wrap} from "@acepad/splashscreen";
export function loadScriptTag(url,attr={}){
/*global define,requirejs*/
    if (attr.type!=="module" && 
        typeof define==="function" && 
        define.amd && 
        typeof requirejs==="function") {
        return new Promise(
            (s)=>requirejs([url],(r)=>s(r)));
    }
    const script = document.createElement('script');
    script.src = url;
    for(let k in attr){
        script.setAttribute(k,attr[k]);
    }
    const urls=url.split("/");
    const mesg=`load ${urls[urls.length-1]}`;
    return wrap(mesg,new Promise(
        function (resolve,reject){
            script.addEventListener("load",resolve);
            script.addEventListener("error",reject);
            document.head.appendChild(script);
    }));
}
