import * as lru from "@hoge1e3/lru";
import * as assert from "assert";
export async function main(){
    await simple.call(this);
    await obj.call(this);
}
async function obj() {
    const l=new lru.Lru({},{key:"id"});
    l.add({note:"test",id:"001"});
    await this.sleep(0.1);
    l.add({note:"desu",id:"002"});
    await this.sleep(0.1);
    l.add({note:"ka?",id:"003"});
    await this.sleep(0.1);
    l.add({note:"desu",id:"002"});
    await this.sleep(0.1);
    for (let e of l) {
        this.echo(e.id, e.note, e.timestamp);
        await this.sleep(0.1);
    }
    console.log(l.raw);
}
async function simple(){
    const l=new lru.Lru();
    l.add("test");
    await this.sleep(0.1);
    l.add("desu");
    await this.sleep(0.1);
    l.add("ka?");
    await this.sleep(0.1);
    l.add("desu");
    await this.sleep(0.1);
    for (let e of l) {
        this.echo(e.key);
        await this.sleep(0.1);
    }
    console.log(l.raw);
}    
