class Document{
    constructor(path,timeStamp,content){
        this.path=path;
        this.timeStamp=timeStamp;
        this.content=content;
        this.deleted=false;
    }
    at(offset){
        return this.content[offset];
    }
}
let thresh=10;
class DocumentSet{
    constructor (){
        this.index=new LetterIndex();
        
    }
}
class LetterIndex{
    constructor(){
        this.map=new Map();    
    }
    addCh(ch,doc,offset){
        let idx=this.map.get(ch);
        if(!idx){
            idx=new DocumentIndex();
            this.map.set(ch,idx);
        }
        idx.add(doc,offset);
        if(idx instanceof DocumentIndex&&
        idx.count()>thresh){
            this.set(ch,idx.toLetterIndex());
        }
    }
    add(doc,offset){
        this.addCh(doc.at(offset),doc,offset+1);
    }
}
class DocumentIndex{
    constructor (){
        this.map=new Map();
    }
    toLetterIndex(){
        let res=new LetterIndex();
        for(let [doc, offsets] of this.map()){
            for (let o of offsets){
                res.add(doc,o);
            }
        }
        return res;
    }
    count(){
        
    }
}