function echo(...args:any[]){
    console.log(...args);
}
function collectWords(words: Set<string>, content: string) {
    // Split the content into words using a regex to handle different delimiters (e.g., spaces, punctuation).
    const extractedWords = content.split(/\W+/);
    // Iterate through the extracted words.
    for (const word of extractedWords) {
        // Add non-empty words to the set.
        if (word.trim()) {
            words.add(word.trim().toLowerCase()); // Convert to lowercase to avoid duplicates.
        }
    }
}
function getFixture(dir:SFile,ds:DocumentSet){
    const s=new Set<string>();
    for(let f of dir.recursive()){
        if(f.ext()!==".js")continue;
        const d=new Document(f.path(),f.text());
        ds.addDocument(d);
        collectWords(s,d.content);
    }
    return s;
}

