let maxlen=32;
//Object.defineProperty(Object.prototype,"toString",{
//    value:
export function toString (){
        let res="[";
        let tag=this[Symbol.toStringTag];
        if(tag && tag!="Object"){
            res+=tag;
        }
        for(let k in this){
            if(this.hasOwnProperty(k)){
                res+=k;
                if(res.length>maxlen-2)break;
                res+=",";
            }
        }
        res=res.replace(/,$/,"");
        res+="]";
        return res;
    }

export function setMaxLength(m){
    maxlen=m;
}