let maxlen=32;
//Object.defineProperty(Object.prototype,"toString",{
//    value:
export function toString (o=this){
    let res="[";
    let tag=o[Symbol.toStringTag];
    if(tag && tag!="Object"){
        res+=tag+" ";
    }
    for(let k in o){
        if(o.hasOwnProperty(k)){
            res+=k;
            if(res.length>maxlen-2)break;
            res+=",";
        }
    }
    res=res.replace(/,$/,"");
    res+="]";
    return res;
}

export function setMaxLength(m){
    maxlen=m;
}