import {solve} from "./solver.js";
export function predicate(n){
    return function (...args){
        return {
            predicateName:n,args
        };
    };
}
export function re(s,i){
    let r="";
    for(;i>0;i--)r+=s;
    return r;
}

function *prtf(outer){
    console.log(re("-",solve.depth),this.args,"\n");
    yield outer;
}

const prt=(...args)=>({solve:prtf,args});
