import {Class} from "@hoge1e3/recursive-safe";
import * as assert from "assert";
export async function main(){
    const data={
        a:["b","c"],
        b:["d"],
        c:["a"],
        d:["b","d"],
    };
    const Traverser=Class({
        main(name){
            let c=[name];
            for(let k of data[name]){
                c=[...c,...this.main(k)];
            }
            return c;
        },
        onLoopDetected(e){
            console.log("loop",e);
            return [];
        }
    });
    const refs=new Traverser().main("a");
    this.echo(...refs);
}    
