// acepad-jq
/*global $*/
export function get(...args){
    return promise($.get(...args));
}
export function post(...args){
    return promise($.post(...args));
}
export function promise(p){
    return new Promise((s,e)=>{
        p.then(s,w(e));    
    });
}
function w(rej){
    return function (_e){
        let e=new Error(_e.responseText);
        e.response=_e;
        return rej(e);
    };
}