// acepad-bottomup
function byts(a,b){
    return tts(b)-tts(a);
}
function tts(f){
    return f.lastUpdate()*(f.isDir()?0.5:1);
}
export default function bottomup(dir,opt={}){
    let excludes=parseExcludes(opt.excludes);
    return loop(dir,excludes,true);
}
function* loop(dir,ex,up){
    if(!dir)return ;
    for(let f of dir.listFiles().sort(byts)){
        if(ex(f))continue;
        if(f.isDir()){
            yield* loop(f,ex);
        }else{
            yield f;
        }
    }
    if(!up) return;
    yield* loop(dir.up(),(f)=>{
        return f.equals(dir)||ex(f);
    },true);
}

function parseExcludes(excludes){
    excludes=excludes||(()=>false);
    if(typeof excludes==="function"){
        return excludes;
    }
    if(typeof excludes.map!=="function"){
        excludes=[excludes];        
    }
    excludes=excludes.map((s)=>{
        if(typeof s==="function"){
            return s;
        }
        if(s instanceof RegExp){
            return f=>s.exec(f.path());
        }
        s=s+"";
        if(s.match(/^\//)){
            return f=>f.path().startsWith(s);
        }else{
            return f=>f.path().includes(s);
        }
    });
    return (f)=>excludes.some(e=>e(f));
}