// acepad-dotenv
import {FS} from "acepad-os";
import {match} from "textmatcher";
export function config({path}){
    if(typeof path==="string"){
        path=FS.get(path);
    }
    if(path.isDir()){
        path=path.rel(".env");
    }
    for(let line of path.lines()){
        match(line,/\s*(\w+)\s*=(.*)/,(m)=>{
            //console.log("setEnv",m[1],m[2]);
            FS.setEnv(m[1],m[2]);
        });
    }
}