import FS from "@hoge1e3/fs";
function file(f){
    const dir=f.up();
    return FS.get("/.meta/").rel(
        dir.relPath(FS.get("/"))
    ).rel("pos.json");
}
export function get(f){
    let row=1;
    let column=1;
    try{
        const o=file(f);
        const d=o.obj()[f.name()];
        row=d.row-0;
        column=d.column-0;
    }catch(e){
    }
    return {row,column};
}
export function set(f,{row,column}){
    try{
        const o=file(f);
        const d=o.obj()[f.name()];
        d.row=row;
        d.column=column;
        o.obj(d);
    }catch(e){
    }
}