/*global $*/
export function tabs(Menu){
    function add(html,cmd,opt={}){
        opt.class="tab";
        Menu.add(html,cmd,opt);
    }     
    function all(){
        return Menu.all(".tab");
    }
    function sort(){
        let a=all().sort((a,b)=>a.order-b.order);
        let y=0;
        for(let e of a){
            $(e.element).css({
               bottom:y 
            });
            y+=e.rect().height;
            console.log(e.rect().height);
        }
    }

} 