// acepad-widget-dialog
import {showWidget} from "acepad-widget";
export let $=window.$;
$.fn.dialog = function(cmd) {
    if(cmd==="close"){
        if(this.__widget)this.__widget.hide();
        return ;
    }
    if(this.__widget){
        this.__widget.show();
    }else{
        this.__widget=showWidget(this);
    }
    //this.css("color", "red");
};
