// acepad-widget-dialog
import {showWidget} from "acepad-widget";
export let $=window.$;
$.fn.dialog = function(cmd) {
    if(cmd==="close"){
        if(this.__widget)this.__widget.hide();
        return ;
    }
    if(this.__widget){
        this.__widget.show();
    }else{
        //console.log("dialog",this);
        //console.log("dialog",this.html());
        let sc=$("<div>").addClass("widget-dialog").
        append(this).attr({title:$(this).attr("title")});
        this.__widget=showWidget(sc);
    }
    //this.css("color", "red");
};
