import {setConfig,getCurrentEditor} from './states.js';
import {exec} from './command.js';
import {changeSession} from './sessions.js';
import {unsetSel} from './editmode.js';
import {modifierStateToInt,setModifier,renderModifierState} from './modifier.js';
export function initConfig(){
    setConfig({
        commands:{
            "ctrl-w":(editor)=>editor.session.foldAll(),
            /*F2(){
                changeSession("*sessions*");
            },
            sessions:{
                bindKey:"ctrl-1",
                exec(){
                    changeSession("*sessions*");
                }
            },*/
            // Find is file related, it is in acepad-witH-fs
            // indent is built in ace editor
            quitEdit(editor){
                unsetSel(editor);
                editor.getSelection().clearSelection();
                setModifier("edit",0);
                renderModifierState();
            },
            unindent(editor){
                //alert("unindent");
                editor.onCommandKey(
                    {},
                    modifierStateToInt({shift:1}), 
                    9);
            },
        },
        menus:{
            ses(){
                changeSession("*sessions*");
            },
            gutt(){
                const editor=getCurrentEditor();
                editor.renderer.setShowGutter(
                    !editor.renderer.getShowGutter());
            },
        },
        jshint:{
            maxerr:10000,
            esnext:false,
            esversion:11,
            undef:true,
        }
    });
}




