import {setConfig,getCurrentEditor} from './states.js';
import {quitEdit} from './editmode.js';
import {modifiers} from '@acepad/keypad';
export function initDefaultConfig(){
    setConfig({
        commands:{
            "ctrl-w":(editor)=>editor.session.foldAll(),
            quitEdit,
            unindent(editor){
                //alert("unindent");
                editor.onCommandKey(
                    {},
                    modifiers.modifierStateToInt({shift:1}), 
                    9);
            },
        },
        menus:{},
        jshint:{
            maxerr:10000,
            esnext:false,
            esversion:11,
            undef:true,
        },
        editor:{
            tabSize:2,
        },
    });
}

