
export const state={};
export function getMainEditor(){
    return state.mainEditor;
}
export function getCurrentEditor(){
    return state.currentEditor;
}

export function setEditor(e){
    state.mainEditor=state.currentEditor=e;
}
export function setCurrentEditor(e){
    state.currentEditor=e;
}
export function setConfig(c){
    state.config=c;
}
export function getConfig(){
    return state.config;
}
