import {getValue, assign} from "pnode-bootloader";
let vConsole;
export function showVConsole(){
    vConsole.show();
}
export function initVConsole(){
    vConsole=vConsole||getValue("vConsole");
    if (vConsole) return;
    if (window.VConsole) {
        //if (document.querySelector("#__vconsole"))return;
       vConsole = new window.VConsole();
       vConsole.hideSwitch();
       assign({vConsole});
    }
}
