// show-vconsole

export let logorg=console.log;
export function showVConsole(){
    const s=document.querySelector(".vc-switch");
    if(!s){
        initVConsole();
        
    } else s.click();
}
export function initVConsole(){
    if (window.VConsole) {
       var vConsole = new window.VConsole();
       const t=setInterval(()=>{
            const s=document.querySelector(".vc-switch");
            if (!s) return;
            s.setAttribute("style","display: none;");
            clearInterval(t);
       },10);
    }
}