import {matcher} from "textmatcher";
import * as assert from "assert";
export function main(){
    let a=["abc","113","---"];
    let r=a.map(e=>
        matcher(e).
        match(/\w+/,(m,w)=>({found:w})).
        elif(/\d+/,(m,d)=>({found:d-10})).
        else(()=>({found:"no"})).found);
        
    assert.equal(r.join(","),"abc,103,no");
    this.echo(r.found);
}