import {match} from "textmatcher";
import * as assert from "assert";
export function main(){
    let a=["abc","113","---"];
    let m=match(
        /\w+/,(m,w)=>(w+"!"),
        /\d+/,(m,d)=>(d-10),
        "no"
    );
    let r=a.map(m);
    assert.equal(r.join(","),"abc!,103,no");
    this.echo(r.join(","));
}