// textmatcher
export function matcher(str,a){
    let param=(typeof str==="string"?{str}:str);
    str=param.str;
    const m={
        match(reg,f){
            let r=reg.exec(str);
            if(r){
                let n=r.index+r[0].length;
                let rest=str.substring(n);
                let a=Array.from(r);
                let nm=m.with({str:rest},a);
                return nm.with(f(nm,...a));
            }else return m;
        },
        with(p,a){
            let pp={};
            Object.assign(pp,m);
            Object.assign(pp,p);
            return matcher(pp,a);    
        },
    };
    Object.assign(m,param);
    if(a){
        for(let i=0;i<a.length;i++)m[i]=a[i];
    }
    return m;
}